// create sbp payment
import { gql } from "@apollo/client";

// correlationId, actionId, paymentId = generated by crypto.randomUUID()

export const GENERATE_SBP_PAYMENT = gql`
  mutation PaymentSBPCreate(
    $correlationId: ID!
    $actionId: ID!
    $paymentId: ID!
    $targetMsisdn: Msisdn
    $amount: Int!
  ) {
    paymentSBPCreate(
      correlation: { correlationId: $correlationId, actionId: $actionId }
      params: { paymentId: $paymentId, amount: $amount, targetMsisdn: $targetMsisdn }
    ) {
      paymentId
      payment {
        id
        createdAt
        orderNumber
        amount
        paymentUrl
        correlation {
          actionId
          correlationId
        }
      }
    }
  }
`;
